mapper.select:
select * from ums_member a
where a.store_id=#{storeId}
AND a.nickname=#{umsMemberNickname}
AND a.id=#{umsMemberId}
AND a.status=#{status_off}
LIMIT 0,1

SELECT b.card_money ,b.card_no ,c.name AS "uniacName" ,GROUP_CONCAT(e.label_name) AS "labelName" ,f.recommendNum AS "recommendNum" ,h.username AS "recommendName" ,a.* FROM ums_member a LEFT JOIN wt_water_card b ON a.id=b.ums_member_id AND b.del_flag='1' LEFT JOIN account_wechats c ON c.uniacid=a.uniacid LEFT JOIN sms_label_member d ON d.member_id=a.id
and d.label_id=#{entity.memberLevelId}
LEFT JOIN sms_label_set e ON d.label_id=e.id LEFT JOIN (select f.member_id ,count(f.id) AS "recommendNum" from ums_member_recommend f GROUP BY f.member_id) f ON f.member_id=a.id LEFT JOIN ums_member_recommend g ON g.member_recommend_id=a.id LEFT JOIN ums_member h ON h.id=g.member_id where 1=1
AND a.username=#{entity.username}
AND a.dealer_id=#{entity.dealerId}
AND a.store_id=#{entity.storeId}
AND a.id=#{entity.id}
AND a.phone=#{entity.phone}
AND b.card_no=#{entity.cardNo}
AND c.name like concat('%',#{entity.uniacName},'%')
AND a.member_level_id=#{entity.memberLevelId}
AND e.label_name like concat('%',#{entity.labelName},'%')
GROUP BY a.id

mapper.delete:
	delete from
    sms_label_member
    where label_id=#{id} AND member_id=#{umsMemberId}
