mapper.update:
UPDATE wt_water_card
SET store_id=#{storeId}
,update_time=NOW()
,update_by=#{updateBy}
WHERE CAST(card_no AS SIGNED) BETWEEN #{sta} and #{end}
AND del_flag =#{delFlag}

mapper.select:
SELECT * FROM wt_water_card
WHERE del_flag =#{data.delFlag}

    AND card_no=#{data.cardNo}

LIMIT 1
